*{
    box-sizing: border-box;
    border-radius: 2px;
}
.box{
    border: 1px solid #E6E6E6;
    text-align: center;
    padding:40px;
    width: 100%;
}
.container{
    padding: 6px 0;
    display: flex;
    text-align:justify;
    justify-content:space-between;
    width: 100%;
    position: relative;
}

.shortbox{
    margin: 2.5px;
    width: 49%;
}
.longbox{
    margin: 0 0 10px 0;
}

.greenInverse{
    background-color:#307A74;
}
.redInverse{
    background-color:#D93526;
}
.blueInverse{
    background-color:#3E96D2;
}


.shortLogo{
    border: none;
    width: 200px;
}
.longLogo{
    border: none;
    width: auto;
    height:56px;
}

.colorCard{
    font-family: sans-serif;
    font-weight: 800;
    font-size: 14px;
    color: white;
    justify-content:space-between;
    width: 19%;
